package com.glass.mouher.ui.mall.home.stores

import android.content.Context
import androidx.databinding.Bindable
import androidx.databinding.Observable
import com.glass.domain.entities.Item
import com.glass.mouher.BR
import com.glass.mouher.ui.base.BaseViewModel
import com.glass.mouher.ui.common.binder.ClickHandler

class StoresViewModel(
    private val context: Context
): BaseViewModel(), ClickHandler<AStoresViewModel> {

    @Bindable
    var openStore: Unit? = null

    @Bindable
    var zoneName: String? = null

    @Bindable
    var items: List<AStoresViewModel> = listOf()
        set(value){
            field = value
            notifyPropertyChanged(BR.items)
        }


    fun initialize(zoneName: String){
        this.zoneName = zoneName
        notifyPropertyChanged(BR.zoneName)
    }

    override fun onResume(callback: Observable.OnPropertyChangedCallback?) {
        addOnPropertyChangedCallback(callback)

        val storesList = mutableListOf<Item>()
        storesList.add(Item(name = "ZARA", imageUrl = "https://gestion.pe/resizer/sfRLQ14ewsCJLKWqdIMwd93fJ2Q=/980x528/smart/arc-anglerfish-arc2-prod-elcomercio.s3.amazonaws.com/public/LWKXDAO2QZEBBBKVXP76K4RNJI.jpg"))
        storesList.add(Item(name = "PULL & BEAR", imageUrl = "https://i0.wp.com/www.biosferaplaza.es/wp-content/uploads/2015/05/pull-bear.jpg?w=1080"))
        storesList.add(Item(name = "H & M", imageUrl = "https://www.modaes.com/files/2020/empresas/h&m/hm-tienda-en-peru-948.jpg"))
        storesList.add(Item(name = "FOREVER 21", imageUrl = "data:image/jpeg;base64,"))
        storesList.add(Item(name = "GUCCI", imageUrl = "https://media.gucci.com/content/StoreDetailStandard_970x400/1548145803/StoreDetailStandard_Store-kuwait-entrance_001_Default.jpg"))
        storesList.add(Item(name = "GUESS", imageUrl = "https://www.vallartadaily.com/wp-content/uploads/2018/10/guess-store.jpg"))

        val viewModels = mutableListOf<AStoresViewModel>()

        storesList.forEach {
            val viewModel = StoreItemViewModel(context = context, menu = it)
            viewModels.add(viewModel)
        }

        items = viewModels
    }

    override fun onPause(callback: Observable.OnPropertyChangedCallback?) {
        items = listOf()
        removeOnPropertyChangedCallback(callback)
        onCleared()
    }

    override fun onClick(viewModel: AStoresViewModel) {
        if(viewModel is StoreItemViewModel){
            notifyPropertyChanged(BR.openStore)
        }
    }
}